from Crypto.Util.number import getStrongPrime, inverse, getRandomNBitInteger

bits = 1024
e = 7
n_samples = 14

p = getStrongPrime(bits, e)
q = getStrongPrime(bits, e)

n = p*q
d = inverse(e, (p-1)*(q-1))

def sign(m):
    return pow(m, d, n)

flag = b"[REDACTED]"  # Find what it should be.
assert len(flag)*8 == bits

print("signatures = [")
print("    " + str(sign(int.from_bytes(flag, byteorder="big"))) + ",")
for i in range(n_samples-1):
    print("    " + str(sign(getRandomNBitInteger(bits))) + ",")
print("]")

